import React from 'react'
import "./category_products.css"

const Category_Products = ({id, title, features, dimensions}) => {
  const dim = ()=>{
    let  dm = []
    
    for(let i = 0; i<dimensions.length; i++){
      
      dm.push(<li>{dimensions[i]}</li>)
      
    }
    return dm
  }
  const featureList = ()=>{
    let  FL = []
    
    for(let i = 0; i<features.length; i++){
      
      FL.push(<li>{features[i]}</li>)
      
    }
    return FL
  }
  return (
    <>
    <div className='product-container'>
    <h2>{title}</h2>
    <div className='post-header-product-info'>
    <figure>
      <img src='https://picsum.photos/150/100' alt={title}/>
    </figure>
    <div className='product-description-container'>
      <h3>Dimensions</h3>
      
      <h3>Features</h3>
      <ul>{featureList()}</ul>
    </div>
    </div>
    </div>
    
    </>
  )
}

export default Category_Products
