import React from 'react'
import "./category_products.css"
import { Link, useNavigate} from "react-router-dom"

const CategoryProducts = ({id, title, features, specs,price}) => {

  let [products, setProducts] = React.useState({errorMessage: '', data: {}})

  let {categoryId} = useParams();
  const navigate = useNavigate();
  React.useEffect(()=>{
    const fetchData = async ()=>{
      const responseObject = await getProductsById(categoryId)
      setProduct(responseObject)
    }
    fetchData()
  },[categoryId])

  const renderProducts = () =>{
    return products.data.map((p) =>{
      <CategoryProducts key={p.id} {...p}>
        {p.title}
        </CategoryProducts>
      }
    )}
  

  
  return (
    <>
    <div>
      {products.errorMessage && <div>Error: {products.errorMessage}</div>}

      {products.data && renderProducts()}
    </div>
    
    </>
  )
}
export default CategoryProducts
